mapper.update:
begin

UPDATE PROD_INST@bill2crm SET STATUS_CD='149999' WHERE PROD_INST_ID IN
(select serv_id  from serv
 where acc_nbr in

    #{tbOBatchsub.accNbr}

 and if_valid='1' and state not in('2HK','2HB')
) and STATUS_CD='949999';

UPDATE BB_SERVICE_RELATION_T@bill2crm SET SERVING_STATUS='149999'
WHERE SERVICE_ID IN

  #{tbOBatchsub.accNbr}

AND IF_VALID=1 and SERVING_STATUS='949999';

end;

mapper.select:
select acc_nbr from serv where acc_nbr in
#{tbOBatchsub.accNbr}
and if_valid='1' and state in('2HI')

select acc_nbr accNbr,state newState from serv where acc_nbr in
#{item.accNbr, jdbcType=VARCHAR}
and if_valid='1' order by state
